import React , { Component } from 'react';
import './Options.css';
class Options extends Component {
  constructor(props) {
    super(props);
    this.state = {
      values: []
    };
  }

  remove_duplicate(data) {
    console.log("into removing duplicates")
    return data
    // return Array.from(new Set(data.map((item) => item)))

  }
  async componentDidMount() {
    try {
        const res = await fetch("https://flaskstockapi.herokuapp.com/api/nfo?fields[nfo]=strategy_id");
        const data = await res.json();

        this.setState({
          values:this.remove_duplicate(data.data.attributes)
        })
        console.log("data printingn",this.state);
    } catch(e) {
        console.error(e);
    }
  }
  render() {
    const optionTemplate = this.state.values.map(v => (
      <option value={v.attributes.strategy_id}>{v.attributes.strategy_name}</option>
    ));

    return (
      <label>
        Pick your favorite Number:
        <div>
        <select value={this.state.value} onChange={this.handleChange}>
          {optionTemplate}
        </select>
        </div>
      </label>
    );
  }
}
export default Options
