import React , { Component } from 'react';
class Options extends Component {
  constructor(props) {
    super(props);
    this.state = {
      values: []
    };
  }

  remove_duplicate(data) {
    console.log("into removing duplicates")
    const unique = data
       .map(e => e["attributes.strategy_id"])

       // store the keys of the unique objects
       .map((e, i, final) => final.indexOf(e) === i && i)

       // eliminate the dead keys & store unique objects
      .filter(e => data[e]).map(e => data[e]);      

    return unique;

  }
  async componentDidMount() {
    try {
        const res = await fetch("https://flaskstockapi.herokuapp.com/api/nfo?fields[nfo]=strategy_id");
        const data = await res.json();

        this.setState({
          values:this.remove_duplicate(data.data)
        })
        console.log("data printingn",data);
    } catch(e) {
        console.error(e);
    }
  }
  render() {
    const optionTemplate = this.state.values.map(v => (
      <option value={v.id}>{v.attributes.strategy_id}</option>
    ));

    return (
      <label>
        Pick your favorite Number:
        <select value={this.state.value} onChange={this.handleChange}>
          {optionTemplate}
        </select>
      </label>
    );
  }
}
export default Options
